import axios from "axios";

const API_URL = "http://localhost:3001/alunos"; // URL da API

const getAlunos = () => axios.get(API_URL);

const getAlunoById = (id) => axios.get(`${API_URL}/${id}`);

const createAluno = (aluno) => axios.post(API_URL, aluno);

const updateAluno = (id, aluno) => axios.put(`${API_URL}/${id}`, aluno);

const deleteAluno = (id) => axios.delete(`${API_URL}/${id}`);

export { getAlunos, getAlunoById, createAluno, updateAluno, deleteAluno };
