// src/components/AlunoList.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getAlunos, deleteAluno } from "../services/AlunoService";
import './AlunoList.css';

function AlunoList() {
  const [alunos, setAlunos] = useState([]);
  const [mediaIRA, setMediaIRA] = useState(0);
  const [colorize, setColorize] = useState(false); // Estado para alternar a coloração

  useEffect(() => {
    loadAlunos();
  }, []);

  const loadAlunos = async () => {
    const result = await getAlunos();
    setAlunos(result.data);
    calcularMediaIRA(result.data); // Calcula a média assim que os alunos são carregados
  };

  const calcularMediaIRA = (alunos) => {
    if (alunos.length === 0) return;
    const somaIRA = alunos.reduce((total, aluno) => total + aluno.IRA, 0);
    const media = somaIRA / alunos.length;
    setMediaIRA(media.toFixed(2)); // Arredonda para 2 casas decimais
  };

  const handleDelete = async (id) => {
    await deleteAluno(id);
    loadAlunos(); // Recarrega a lista após exclusão
  };

  const toggleColorize = () => {
    setColorize(!colorize); // Alterna o estado de coloração
  };

  return (
    <div>
      <h2>Lista de Alunos</h2>
      <Link to="/create" className="btn btn-primary mb-3">Adicionar Aluno</Link>
      <button onClick={toggleColorize} className="btn btn-secondary mb-3">
        {colorize ? "Remover Cores" : "Aplicar Cores"}
      </button>
      <table className="table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Curso</th>
            <th>IRA</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {alunos.map((aluno) => (
            <tr
              key={aluno.id}
              className={
                colorize
                  ? aluno.IRA < mediaIRA
                    ? "below-average"
                    : "above-average"
                  : ""
              }
            >
              <td>{aluno.nome}</td>
              <td>{aluno.curso}</td>
              <td>{aluno.IRA}</td>
              <td>
                <Link to={`/edit/${aluno.id}`} className="btn btn-warning mr-2">
                  Editar
                </Link>
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(aluno.id)}
                >
                  Excluir
                </button>
              </td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          <tr className="media-row">
            <td colSpan="2"><strong>Média dos IRAs:</strong></td>
            <td><strong>{mediaIRA}</strong></td>
            <td></td>
          </tr>
        </tfoot>
      </table>
    </div>
  );
}

export default AlunoList;
