import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { createAluno, getAlunoById, updateAluno } from "../services/AlunoService";

function AlunoForm() {
  const [aluno, setAluno] = useState({
    nome: "",
    curso: "",
    IRA: ""
  });

  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      loadAluno();
    }
  }, [id]);

  const loadAluno = async () => {
    const result = await getAlunoById(id);
    setAluno(result.data);
  };

  const handleChange = (e) => {
    setAluno({
      ...aluno,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (id) {
      await updateAluno(id, aluno);
    } else {
      await createAluno(aluno);
    }
    navigate("/");
  };

  return (
    <div>
      <h2>{id ? "Editar Aluno" : "Adicionar Aluno"}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome</label>
          <input
            type="text"
            className="form-control"
            name="nome"
            value={aluno.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Curso</label>
          <input
            type="text"
            className="form-control"
            name="curso"
            value={aluno.curso}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>IRA</label>
          <input
            type="number"
            className="form-control"
            name="IRA"
            value={aluno.IRA}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success">
          {id ? "Atualizar" : "Adicionar"}
        </button>
      </form>
    </div>
  );
}

export default AlunoForm;
